    #[test]
    fn test_u64_to_ar() {
        let value = 1000000000000;
        let ar = u64_to_ar(value);
        assert_eq!(ar, "1 AR");

        let value = 1000000000001;
        let ar = u64_to_ar(value);
        assert_eq!(ar, "1.000000000001 AR");

        let value = 1000000000000000000;
        let ar = u64_to_ar(value);
        assert_eq!(ar, "1000000 AR");
    }
