use crate::common::types::{Ptr, PtrBytes, PtrString};
use crate::common::ur::{UREncodeResult, FRAGMENT_MAX_LENGTH_DEFAULT};
use crate::common::utils::recover_c_char;
use crate::extract_array;
use alloc::vec::Vec;
use app_arweave::generate_public_key_from_primes;
use cty::uint32_t;
use hex;
use ur_registry::arweave::arweave_crypto_account::ArweaveCryptoAccount;
use ur_registry::traits::{RegistryItem, To};

fn vec_to_array(vec: Vec<u8>) -> Result<[u8; 4], &'static str> {
    if vec.len() == 4 {
        let mut arr = [0u8; 4];
        for (i, byte) in vec.into_iter().enumerate() {
            arr[i] = byte;
        }
        Ok(arr)
    } else {
        Err("Vec<u8> must contain exactly 4 elements")
    }
}

#[no_mangle]
pub unsafe extern "C" fn get_connect_arconnect_wallet_ur(
    master_fingerprint: PtrBytes,
    master_fingerprint_length: uint32_t,
    p: PtrBytes,
    p_len: uint32_t,
    q: PtrBytes,
    q_len: uint32_t,
) -> Ptr<UREncodeResult> {
    let mfp = extract_array!(master_fingerprint, u8, master_fingerprint_length as usize);
    let p = extract_array!(p, u8, p_len as usize);
    let q = extract_array!(q, u8, q_len as usize);
    let public_key = generate_public_key_from_primes(p, q).unwrap();
    let device = Option::None;
    let arweave_account = ArweaveCryptoAccount::new(
        vec_to_array(mfp.to_vec()).unwrap(),
        public_key.to_vec(),
        device,
    );
    UREncodeResult::encode(
        arweave_account.try_into().unwrap(),
        ArweaveCryptoAccount::get_registry_type().get_type(),
        FRAGMENT_MAX_LENGTH_DEFAULT,
    )
    .c_ptr()
}

#[no_mangle]
pub unsafe extern "C" fn get_connect_arconnect_wallet_ur_from_xpub(
    master_fingerprint: PtrBytes,
    master_fingerprint_length: uint32_t,
    xpub: PtrString,
) -> Ptr<UREncodeResult> {
    let mfp = extract_array!(master_fingerprint, u8, master_fingerprint_length as usize);
    let xpub = recover_c_char(xpub);
    let device = Option::None;
    let arweave_account = ArweaveCryptoAccount::new(
        vec_to_array(mfp.to_vec()).unwrap(),
        hex::decode(xpub).unwrap(),
        device,
    );
    UREncodeResult::encode(
        arweave_account.try_into().unwrap(),
        ArweaveCryptoAccount::get_registry_type().get_type(),
        FRAGMENT_MAX_LENGTH_DEFAULT,
    )
    .c_ptr()
}

#[cfg(test)]
mod tests {
    use super::*;
    extern crate std;

    #[test]
    fn test_get_connect_arconnect_wallet_ur() {
        let mut master_fingerprint = [52, 74, 47, 03];
        let master_fingerprint_length = 4;
        let mut p = hex::decode("").unwrap();
        let mut q = hex::decode("").unwrap();
        unsafe {
            let result = get_connect_arconnect_wallet_ur(
                master_fingerprint.as_mut_ptr(),
                master_fingerprint_length,
                p.as_mut_ptr(),
                p.len() as uint32_t,
                q.as_mut_ptr(),
                q.len() as uint32_t,
            );
            let data = (*result).data;
            let data = std::ffi::CStr::from_ptr(data).to_str().unwrap();
            assert_eq!(data, "UR:ARWEAVE-CRYPTO-ACCOUNT/1-");
        }
    }
}
